import React from 'react';
import styled from 'styled-components';

function Button(props) {
    return (
        <>
           {props.size=='l'?<StBtnL>{props.children}</StBtnL>:(props.size=='m'?<StBtnM>{props.children}</StBtnM>:<StBtnS>{props.children}</StBtnS>)} 
        </>
    );
}

// 스타일
const StBtnL = styled.button`
  height : 50px;
  width : 200px;
  border-radius: 8px;
  border: 3px solid ${(props)=>{
    return props.type == "primary"?'#55EFC4':'#FAB1A0'
  }};
  background-color: rgb(255, 255, 255);
  font-weight: 600;
  cursor: pointer;
`

const StBtnM = styled.button`
  height : 45px;
  width : 130px;
  border-radius: 8px;
  border: 3px solid ${(props)=>{
    return props.type == "primary"?'#55EFC4':'#FAB1A0'
  }};
  background-color: rgb(255, 255, 255);
  cursor: pointer;
`

const StBtnS = styled.button`
  height : 40px;
  width : 100px;
  border-radius: 8px;
  border: 3px solid ${(props)=>{
    return props.type == "primary"?'#55EFC4':'#FAB1A0'
  }};
  background-color: rgb(255, 255, 255);
  cursor: pointer;
`

export default Button;
