import styled from "styled-components";

export const StTitle = styled.h2`
font-size:20px;
font-weight:700;
`

export const StModalOverlay = styled.div`
width:100vw;
height:100vh;
top:0;
left:0;
right:0;
bottom:0;
position:fixed;
background-color:rgba(49,49,49,0.8);
`
// export const StOverlay = styled.div`
// background-color:rgba(49,49,49,0.8);
// `



export const StModalContent = styled.div`
position:relative;
top:40%;
left:50%;
transform:translate(-50%,-50%);
line-height:1.4%;
background-color: #f1f1f1;
padding: 14px 28px;
border-radius:3px;
max-width:600px;
min-width:300px;
height:200px;
`

export const StCloseModal = styled.button`
position:absolute;
top:30%
`
