  const toggleModal = ()=>{
    setModal(!modal)
  }
  return (
    <>
    <StTitle>modal</StTitle>
    <button onClick={toggleModal}>open modal</button>
    <button>open modal</button>

    {modal && <StModalOverlay>
    <StModalContent>
      <h2>hello</h2>
      <p>이것은 모달입니다만</p>
      <StCloseModalButton onClick={toggleModal}>CLOSE</StCloseModalButton>
    </StModalContent>
    </StModalOverlay>}

    </>
  )
}

export default Modal
