import React, { useState } from 'react'
import { StCloseModalButton, StContent, StModalButton, StModalContent, StModalOverlay, StTitle } from './StyledComponents'

function Modal() {



  const [modal, setModal] = useState(false)

  const toggleModal = ()=>{
    setModal(!modal)
  }
  return (
    <>
    <StTitle>modal</StTitle>
    <button onClick={toggleModal}>open modal</button>
    <button>open modal</button>

    {modal && <StModalOverlay>
    <StModalContent>
      <StContent>닫기와 확인 버튼 2개가 있고, 외부 영역을 눌러도 모달이 닫히지 않아요.</StContent>
      <StModalButton>
      <StCloseModalButton onClick={toggleModal}>닫기</StCloseModalButton>
      <StCloseModalButton >확인</StCloseModalButton>
      </StModalButton>
    </StModalContent>
    </StModalOverlay>}

    </>
  )
}

export default Modal
