import React from 'react';
import styled from 'styled-components';

function Button(props) {
    console.log(props)
    return (
        <>
            {
                props.size == 'l' ? <StBtnL type={props.type}>{props.children}{arrowIcon}</StBtnL> : (props.size == 'm' ? <StBtnM type={props.type} >{props.children}</StBtnM> : <StBtnS type={props.type}>{props.children}</StBtnS>)
            }
        </>
    );
}

// 스타일
const StBtnL = styled.button`
  height : 50px;
  width : 200px;
  border-radius: 8px;
  border: 3px solid ${(props) => {
        return props.type == "primary" ? '#55EFC4' : '#FAB1A0'
    }};
  background-color: rgb(255, 255, 255);
  color : ${(props) => { return props.type == 'negative' ? '#D63031' : '#000' }};
  font-weight: 600;
  cursor: pointer;
`

const StBtnM = styled.button`
  height : 45px;
  width : 130px;
  border-radius: 8px;
  border: none;
  background-color: ${(props) => {
        return props.type == "primary" ? '#55EFC4' : '#FAB1A0'
    }};
  color : ${(props) => { return props.type == 'negative' ? '#D63031' : '#000' }};
  cursor: pointer;
`

const StBtnS = styled.button`
  height : 40px;
  width : 100px;
  border-radius: 8px;
  border: none;
  background-color: ${(props) => {
        return props.type == "primary" ? '#55EFC4' : '#FAB1A0'
    }};
  color : ${(props) => { return props.type == 'negative' ? '#D63031' : '#000' }};
  cursor: pointer;
`

export default Button;
